var expect = require('chai').expect;
var MapObject = require('../lib/map-object');

describe('#map object', function() {
   it('convert source to dest object', function() {

      // source object
      var sourceValue = 'source_value';
      var sourceProperty = 'source_property';
      var source = {};
      source[sourceProperty] = sourceValue;

      // map object
      var map = {};
      var destProperty = 'des_property';
      map[sourceProperty] = destProperty;

      // convert
      var converter = new MapObject(map);
      var dest = converter.convert(source);

      // expect
      expect(dest).to.have.property(destProperty);
      expect(dest[destProperty]).to.equal(sourceValue);
   });
});
